mapper.select:
select
    a.*,
    if(a.type=1,'内部讲师','外部讲师') as typeName,
    if(a.status=0,'失效','有效') as statusName,
    if(a.sex="F",'女','男') as sexName,
    b.name as levelName, c.short_val AS educationLevelName
from train_base_teacher a
    left join train_constant b on a.level = b.id
    left join staff_dict c on a.education_level = c.type_val and c.type_name = "C_EDCT_BAKG"
where
    a.is_delete = 0 and a.id = #{id}

SELECT a.number AS teacherNumber, a.name AS teacherName, CONCAT(b.level, "-", c.name) AS levelName, b.start_time, b.end_time, b.fee, b.instructions FROM train_base_teacher a LEFT JOIN train_base_teacher_progress b ON a.id = b.teacher_id LEFT JOIN train_constant c ON b.level = c.id WHERE a.is_delete = 0
AND a.name LIKE '%${name}%'
AND a.type = #{type}
AND a.level = #{level}
AND a.organization_name LIKE '%${organizationName}%'
ORDER BY a.id DESC

SELECT a.number AS teacherNumber, a.name AS teacherName, c.number AS courseNumber, c.name AS courseName, CONCAT(c.category, "-", d.name) AS courseCateName, c.class_hours, c.instructions FROM train_base_teacher a LEFT JOIN train_base_course_teacher b ON a.id = b.teacher_id LEFT JOIN train_base_course c ON b.course_id = c.id LEFT JOIN train_constant d ON c.category = d.id WHERE a.is_delete = 0
AND a.name LIKE '%${name}%'
AND a.type = #{type}
AND a.level = #{level}
AND a.organization_name LIKE '%${organizationName}%'
ORDER BY a.id DESC

SELECT a.number AS teacherNumber, a.name AS teacherName, b.course_start_time AS startTime, b.course_end_time AS endTime, b.teacher_score, c.number AS courseNumber, c.name AS courseName, c.class_hours AS classHour, CONCAT(c.category, "-", d.name) AS courseCateName, e.train_name FROM train_base_teacher a LEFT JOIN train_base_teacher_taught b ON a.id = b.teacher_id LEFT JOIN train_base_course c ON b.course_id = c.id LEFT JOIN train_constant d ON c.category = d.id LEFT JOIN train_programs e ON b.programs_id = e.id WHERE a.is_delete = 0
AND a.name LIKE '%${name}%'
AND a.type = #{type}
AND a.level = #{level}
AND a.organization_name LIKE '%${organizationName}%'
ORDER BY a.id DESC
