mapper.select:
select

from train_constant

    is_deleted = 0

        and type = #{type}


        and pid = #{pid}


        and status = #{status}


        and name like concat('%', #{name}, '%')


        and name in

            #{nameList}



order by id asc

select id, pid, name, type, case type when 1 then "课程类别" when 2 then "培训需求依据" when 3 then "培训考核方法" when 4 then "讲师等级" when 5 then "机构类型" when 6 then "计划类型" when 7 then "职序与学习方向" when 8 then "培训科目" when 9 then "机构等级" when 10 then "学习方式" end as typeName, status, if(status=1,'启用','停用') as statusName, FROM_UNIXTIME(create_time) as create_time from train_constant
is_deleted = 0
and type = #{type}
and pid = #{pid}
and status = #{status}
and name like '%${name}%'
order by type asc, id asc

SELECT tc.id, tc.name, CASE tc.type WHEN 1 THEN "课程类别" WHEN 2 THEN "培训需求依据" WHEN 3 THEN "培训考核方法" WHEN 4 THEN "讲师等级" WHEN 5 THEN "机构类型" WHEN 6 THEN "计划类型" WHEN 7 THEN "职序与学习方向" WHEN 8 THEN "培训科目" WHEN 9 THEN "机构等级" WHEN 10 THEN "学习方式" END AS typeName, CONCAT(sps.id, "-", sps.descr) AS secCodeName FROM train_constant tc LEFT JOIN staff_posn_sec sps ON tc.pid = sps.id AND tc.type = 7
tc.status = 1 AND tc.is_deleted = 0
AND tc.type = #{type}
AND tc.pid = #{pid}
AND tc.name LIKE '%${name}%'
ORDER BY tc.type ASC, tc.id ASC
